mapper.insert:
/*
    [UserMapper.add] 사용자 추가
*/
INSERT INTO MEMBERS (
       member_name
     , company
     , certification_dt
     , phone_number
     , email
     , department
     , created_id
     , created_dt
     ) VALUES (
       #{memberName}
     , #{company}
     , #{certificationDt}
     , #{phoneNumber}
     , #{email}
     , #{department}
     , #{createdId}
     , CURRENT_TIMESTAMP
)

mapper.update:
/* [UserMapper.modify] 사용자 정보 수정 */ UPDATE MEMBERS
member_name = #{memberName},
phone_number = #{phoneNumber},
email = #{email},
WHERE member_idx = #{memberIdx}
